package 연산자;

public class 산출연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 산술연산자(+ - * / %)
		
		int x = 200;
		int y = 100;
		
		System.out.println("두 수의 합은"+ (x + y));
		System.out.println("두 수의 차는"+ (x - y));
		System.out.println("두 수의 곱은"+ (x * y));
		System.out.println("두 수의 나눗샘은"+ (x / y));
	}

}
