package 제어문.조건문;

public class ifelseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "자바";
		
		if(name.equals("자바")) {
			System.out.println("1101호로 가세요!");
		}else if (name.equals("파이썬")) {
			System.out.println("1101호로 가세요!");
		}else if (name.equals("리눅")) {
			System.out.println("1101호로 가세요!");
		}else {
			System.out.println("카운터로 가세요!");
		}
	}

}
